import React, { useEffect, useMemo, useRef, useState } from "react";
import Nav from "./Nav";
import "./Banner.css";

//  this file includes nav bar tag so as to make of its functionality (background black when )

const Banner = React.forwardRef((targetRef) => {
  return (
    <>
      <header
        ref={targetRef}
        className="banner"
        style={{
          backgroundSize: "cover",
          backgroundImage:
            'url("https://www.bajajfinservmarkets.in/content/dam/emistoremarketplace/index/20-02-2020/netflix/Netflix-Banner_1080x419.jpg?impolicy=pqmedium")',
          backgroundPosition: "center center",
        }}
      >
        <div className="banner_contents">
          <h1 className="banner_title">Movie Name</h1>
          <div className="banner_buttons">
            <button className="banner_button">Play</button>
            <button className="banner_button">My List</button>
          </div>
          <h1 className="banner_description">This is the test Description</h1>
        </div>
        <div className="banner_fadebutton"></div>
      </header>
    </>
  );
});

export default Banner;
