import React, { useEffect, useMemo, useRef, useState } from "react";
import "./Banner.css";

function Banner() {
  // --------------------------------------------------- Nav Background colour change

  const targetRef = useRef(null);
  const [show, handleShow] = useState(false);

  const callBackFunction = (entries) => {
    const [entry] = entries;
    handleShow(entry.isIntersecting);
  };

  const options = useMemo(() => {
    return {
      root: null,
      rootMargin: "0px",
      threshold: 0.3,
    };
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(callBackFunction, options);
    const currentTarget = targetRef.current;
    if (currentTarget) observer.observe(currentTarget);

    return () => {
      if (currentTarget) observer.unobserve(currentTarget);
    };
  }, []);
  // ---------------------------------------------------

  return (
    <>
      <header
        className="banner"
        style={{
          backgroundSize: "cover",
          backgroundImage:
            'url("https://upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Black_flag.svg/1200px-Black_flag.svg.png")',
          backgroundPosition: "center center",
        }}
      >
        <div className="banner_contents">
          <h1 className="banner_title">Movie Name</h1>
          <div className="banner_buttons">
            <button className="banner_button">Play</button>
            <button className="banner_button">My List</button>
          </div>
          <h1 className="banner_description">This is the test Description</h1>
        </div>
        <div className="banner_fadebutton"></div>
      </header>
    </>
  );
}

export default Banner;
